INSERT INTO simple_questionnaire VALUES('secondary-students', 'response.name', 'Cuestionario para alumnos de segundo de secundaria', '{ "text": "En este año escolar has participado en el Programa de Educación Social y Financiera. Por eso queremos saber tu opinión sobre lo que viste durante el año en el programa, respecto al dinero y cómo usarlo. No hay respuestas correctas o incorrectas, te pedimos responder lo que opinas con honestidad y confianza.", "questions": [ { "id": "name", "displayName": "Escribe tu nombre:", "type": "text" }, { "id": "age", "displayName": "¿Cuántos años tienes?", "type": "number" }, { "id": "gender", "displayName": "Eres", "type": "options", "options": [ "Hombre", "Mujer" ] }, { "id": "sameSchoolFirst", "displayName": "¿Estuviste en esta escuela en primero de secundaria?", "type": "options", "options": [ "Sí", "No" ] }, { "id": "knowCharacter", "displayName": "¿Conoces a este personaje?", "type": "options", "options": [ "Sí", "No" ] }, { "id": "characterName", "displayName": "¿Cómo se llama?", "type": "text" }, { "id": "obtainMoney", "displayName": "¿De qué forma obtienes dinero? (Puedes marcar varias opciones.)", "type": "options", "options": [ "Mis papás me dan dinero para la escuela.", "Mis papás me dan domingo.", "Algunos familiares me dan dinero.", "Ayudo en la casa o en el negocio familiar.", "Mis papás me dejan tener un pequeño trabajo.", { "name": "Otra, ¿cuál?", "other": true } ] }, { "id": "enoughMoney", "displayName": "¿Cómo manejas tu dinero? Manejo mi dinero de tal forma que me alcanza para lo que yo quiero comprar.", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "enoughPlanning", "displayName": "¿Cómo manejas tu dinero? Planifico mis gastos.", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "enoughExpenseControl", "displayName": "¿Cómo manejas tu dinero? Llevo el control de mis gastos.", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "expendImmediately", "displayName": "Cuando recibo dinero me lo gasto enseguida", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "planThink", "displayName": "Cuando recibo dinero pienso y planeo qué voy a hacer con él", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "savePiggyBank", "displayName": "Cuando recibo dinero lo guardo en mi alcancía", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "saveAndExpend", "displayName": "Cuando recibo dinero gasto una parte y ahorro otra parte.", "type": "options", "options": [ "Siempre", "Algunas", "Nunca" ] }, { "id": "whatsANeed", "displayName": "¿Qué es una necesidad?", "type": "options", "options": [ "Cosas que quiero, pero puedo esperar para comprarlas.", "Algo muy importante para la vida diaria.", "No lo sé." ] }, { "id": "whatsADesire", "displayName": "¿Qué es un deseo?", "type": "options", "options": [ "Cosas que quiero, pero puedo esperar para comprarlas.", "Algo muy importante para la vida diaria.", "No lo sé." ] }, { "id": "whatIsSaving", "displayName": "Explica a un amigo, ¿qué es el ahorro?", "type": "options", "options": [ "Gastarnos todo el dinero que obtenemos en cosas que necesitamos o nos gustan.", "No comprarnos cosas que queremos tener.", "Guardar parte del dinero que recibimos para poder comprarnos algo en el futuro.", "No lo sé." ] }, { "id": "whyIsSavingImportant", "displayName": "¿Por qué es importante ahorrar?", "type": "options", "options": [ "Porque me lo dicen mis papas o mis maestros.", "Porque así siempre voy a tener dinero cuando lo necesite.", "Porque así no pierdo mi dinero.", "No lo sé.", { "name": "Otra, ¿cuál?", "other": true } ] }, { "id": "collectiveSavings", "displayName": "¿Qué es el ahorro colectivo?", "type": "options", "options": [ "Guardar dinero o cosas de valor, en una alcancía o cuenta personal.", "Guardar dinero junto con el de otros compañeros, amigos o personas en un mismo lugar." ] }, { "id": "individualSavings", "displayName": "¿Qué es el ahorro individual?", "type": "options", "options": [ "Guardar dinero o cosas de valor, en una alcancía o cuenta personal.", "Guardar dinero junto con el de otros compañeros, amigos o personas en un mismo lugar." ] }, { "id": "youSave", "displayName": "¿Tú ahorras dinero?", "type": "options", "options": [ "Sí", "No" ] }, { "id": "iSaveSchoolMoney", "displayName": "Yo ahorro cuando me dan dinero para la escuela.", "type": "options", "options": [ "Siempre", "Algunas veces", "Nunca" ] }, { "id": "iSaveAllowanceMoney", "displayName": "Yo ahorro cuando me dan domingo.", "type": "options", "options": [ "Siempre", "Algunas veces", "Nunca" ] }, { "id": "iSaveFamilyMoney", "displayName": "Yo ahorro cuando algunos familiares me dan dinero.", "type": "options", "options": [ "Siempre", "Algunas veces", "Nunca" ] }, { "id": "iSaveFamilyBusinessMoney", "displayName": "Yo ahorro cuando ayudo en la casa o en el negocio familiar.", "type": "options", "options": [ "Siempre", "Algunas veces", "Nunca" ] }, { "id": "iSaveBusinessMoney", "displayName": "Yo ahorro cuando me pagan en el trabajo que tengo.", "type": "options", "options": [ "Siempre", "Algunas veces", "Nunca" ] }, { "id": "iSaveOtherMoney", "displayName": "Yo ahorro con otra opción, ¿cuál?", "type": "other", "optional": true }, { "id": "savingAmount", "displayName": "¿Cuánto llevas ahorrado hasta el día de hoy?", "type": "options", "options": [ "De $1 a $200", "De $201 a $500", "De $501 a 1,000", "Más de $1,001", "No tengo ahorro" ] }, { "id": "savedAmount", "displayName": "¿Cómo sabes cuánto dinero tienes ahorrado?", "type": "options", "options": [ "Llevo una libreta de ahorro.", "Mis padres me dicen cuanto llevo ahorrado.", "Lo cuento cada vez que guardo algo.", "No lo sé.", "No tengo ahorro." ] }, { "id": "savingReasons", "displayName": "¿Para qué estás ahorrando tu dinero?", "type": "multioptions", "options": [ "Juguetes o videojuegos", "Salir de paseo con mi familia o amigos", "Comprarme ropa", "Comprarme dulces, jugos o refresco", "Hacer un regalo", "Comprar música o películas", "Dárselo a alguien de mi familia", "Comprar cosas pasa mi mascota", { "name": "Otra, ¿cuál?", "other": true } ] }, { "id": "savingTime", "displayName": "¿En cuánto tiempo vas a poder juntarlo?", "type": "options", "options": [ "En una semana.", "En un mes.", "En un año.", "En más de un año.", "No ahorro.", "No lo sé", { "name": "Otra, ¿cuál?", "other": true } ] }, { "id": "whereSaveMoney", "displayName": "¿En dónde guardas tu dinero?", "type": "options", "options": [ "En mi alcancía individual", "En una alcancía colectiva", "En una cuenta bancaria", { "name": "Otra, ¿cuál?", "other": true }, "No ahorro" ] }, { "id": "keepSavingsBook", "displayName": "¿Llevas una libreta de ahorro?", "type": "options", "options": [ "Sí", "No" ] }, { "id": "savingExamples", "displayName": "¿Podrías escribir algunos ejemplos de lo que apuntas en tu libreta de ahorro?", "type": "textarea", "optional": true }, { "id": "savingTimes", "displayName": "¿Cuántas veces has logrado tu meta de ahorro?", "type": "options", "options": [ "Siempre", "Algunas veces", "Una vez", "Nunca", "No tengo meta de ahorro" ] }, { "id": "goalFeelings", "displayName": "¿Cómo te sientes de poder alcanzar tus metas?", "type": "options", "options": [ "Feliz", "Triste", "Molesto", "No lo sé" ] }, { "id": "talkMoneyExpenses", "displayName": "¿En tu familia hablan de dinero? Platico con mi papá o mamá sobre el dinero y los gastos de la casa.", "type": "options", "options": [ "Sí", "No" ] }, { "id": "talkMoneySavings", "displayName": "¿En tu familia hablan de dinero? Platico con mi papá o mamá sobre cómo se puede ahorrar dinero.", "type": "options", "options": [ "Sí", "No" ] }, { "id": "talkMoneyGoals", "displayName": "¿En tu familia hablan de dinero? Platico con mi papá o mamá sobre nuestras metas de ahorro.", "type": "options", "options": [ "Sí", "No" ] }, { "id": "parentSavings", "displayName": "¿Sabes si tus padres están