INSERT INTO simple_questionnaire VALUES('parents-account', 'response.name', 'Encuesta para padres de familia (Mi cuenta bancaria)', '{ "text": "Usted a principio de este año abrió una cuenta bancaria con el apoyo de Fundación EDUCA. Nos interesa saber en qué medida la cuenta ha sido útil para usted y su familia y por lo tanto en qué medida la ha usado. Las preguntas se refieren a esta cuenta que acaba de abrir. Le agradecemos mucho su opinión, porque nos permite entender como apoyarlos de mejor manera con las cuentas bancarias que abrieron.", "questions": [ { "id": "stillHaveBankAccount", "displayName": "¿Usted continua usando su cuenta bancaria que abrió con apoyo de Fundación Educa?", "type": "options", "options": [ "Sí", "No", "No lo sé" ] }, { "id": "whyNoAccount", "displayName": "En caso de no usar su cuenta, por favor explique por qué:", "type": "options", "optional": true, "options": [ "No me es útil", "No es lo que esperaba", "No sé como utilizarla", { "name": "Otra, ¿cuál?", "other": true } ] }, { "id": "whoUsesIt", "displayName": "¿Quién utiliza la cuenta?", "type": "options", "optional": true, "options": [ "Mi hijo/a", "Yo", "Ambos", "Ninguno" ] }, { "id": "frequency", "displayName": "Indique la frecuencia con la que se usa la cuenta:", "type": "options", "options": [ "Una vez a la semana", "Una vez cada quince días", "Una vez al mes", "Dos o tres veces al mes", { "name": "Otra, ¿cuál?", "other": true }, "No lo sé" ] }, { "id": "cardIssued", "displayName": "¿Con la cuenta que abrió le poporcionaron un tarjeta?", "type": "options", "options": [ "Sí, una tarjeta de débito", "Sí, una tarjeta de crédito", "Sí, pero no se que tipo de tarjeta es", "No lo sé", "No manejo tarjeta con mi cuenta" ] }, { "id": "accountUsage", "displayName": "¿Para qué utiliza usualmente la cuenta?", "type": "options", "options": [ "Comprar cosas", "Ahorrar o guardar dinero", "Pagar servicios", "Recibir dinero", "Invertir dinero en el banco", { "name": "Otra, ¿cuál?", "other": true }, "No lo sé" ] }, { "id": "accountSavingAmount", "displayName": "En caso de utilizar la cuenta para ahorrar o guardar dinero ¿cuánto dinero ahorra o guarda aproximadamente al mes?", "type": "options", "options": [ "Entre 0 a 250 pesos", "Entre 251 y 500 pesos", "Entre 501 y 1,000 pesos", "Más de 1,000 pesos", "No lo sé", "No quiero contestar esta pregunta" ] }, { "id": "worthSavingsAccount", "displayName": "¿Cree que valió la pena abrir la cuenta?", "type": "options", "options": [ { "name": "Sí ¿Por qué?", "other": true }, { "name": "No ¿Por qué?", "other": true } ] }, { "id": "advantages", "displayName": "¿Qué ventajas le ha traído abrir una cuenta? (Varias respuestas posibles.)", "type": "multioptions", "options": [ "Empezar a ahorrar o guardar dinero", "Ahorrar o guardar más que antes", "Controlar los gastos", "Manejar mejor el dinero", "Inspirar a mi hijo/hija a ahorrar dinero", { "name": "Otra", "other": true }, "Ninguna" ] }, { "id": "timeSatisfaction", "displayName": "Indique su satisfacción con la institución financiera, en la que abrió su cuenta (Horario de Atención)", "type": "options", "options": [ "Muy satisfecho", "Satisfecho", "Poco satisfecho", "Nada satisfecho" ] }, { "id": "atmSatisfaction", "displayName": "Indique su satisfacción con la institución financiera, en la que abrió su cuenta (El acceso a sucursales o cajeros)", "type": "options", "options": [ "Muy satisfecho", "Satisfecho", "Poco satisfecho", "Nada satisfecho" ] }, { "id": "clientSatisfaction", "displayName": "Indique su satisfacción con la institución financiera, en la que abrió su cuenta (Atención al cliente)", "type": "options", "options": [ "Muy satisfecho", "Satisfecho", "Poco satisfecho", "Nada satisfecho" ] }, { "id": "comissionSatisfaction", "displayName": "Indique su satisfacción con la institución financiera, en la que abrió su cuenta (Comisiones)", "type": "options", "options": [ "Muy satisfecho", "Satisfecho", "Poco satisfecho", "Nada satisfecho" ] }, { "id": "accountImportan