INSERT INTO simple_questionnaire VALUES('secondary_secondEdition', 'response.name', 'Segunda Ronda: Preguntas para alumnos y alumnas de segundo de secundaria',' {"text":"En este año escolar estás participando en el Programa de Educación Social y Financiera. Por eso queremos saber tu opinión respecto al ahorro. No hay respuestas correctas o incorrectas, te pedimos responder lo que opinas con honestidad y confianza.","questions":[{"id":"1secondary","displayName":"Escribe tu nombre","type":"text"},{"id":"2secondary","displayName":"Cuando recibo dinero me lo gasto enseguida.","type":"options","options":["Siempre","Algunas veces","Nunca"]},{"id":"3secondary","displayName":"Cuando recibo dinero pienso y planeo qué voy a hacer con él.","type":"options","options":["Siempre","Algunas veces","Nunca"]},{"id":"4secondary","displayName":"Cuando recibo dinero lo guardo en mi alcancía.","type":"options","options":["Siempre","Algunas veces","Nunca"]},{"id":"5secondary","displayName":"Cuando recibo dinero gasto una parte y ahorro otra parte.","type":"options","options":["Siempre","Algunas veces","Nunca"]},{"id":"6secondary","displayName":" ¿Tú ahorras dinero?","type":"options","options":["Si","No"]},{"id":"7secondary","displayName":"¿Cuánto llevas ahorrado hasta el día de hoy?","type":"options","options":["De $1 a $200.","De $201 a $500.","De $501 a 1,000.","Más de $1,001.","No tengo ahorro."]},{"id":"8secondary","displayName":"¿En dónde guardas tu dinero?","type":"multioptions","options":["En mi alcancía individual.","En una alcancía colectiva.","En una cuenta bancaria.","Otra, ¿cuál?","No ahorro."]},{"id":"9secondary","displayName":"¿Llevas una libreta de ahorro?   ","type":"options","options":["Si","No"]},{"id":"10secondary","displayName":"¿Podrías escribir algunos ejemplos de lo que apuntas en tu libreta de ahorro?","type":"text"},{"id":"11secondary","displayName":"¿En tu familia hablan de dinero? Platico con mi mamá o papá sobre el dinero y los gastos de la casa.","type":"optio