INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac1.1', 'La Asamblea General de Asociados', 'ac-2018', 'Órgano de Gobierno', 'La Asamblea General de Asociados', '1', '1.1', ' {"questions":[{"id":"1","question":"La Asamblea General de Asociados está conformada por al menos dos personas que participan activamente en la toma de decisiones de la Institución.","priority":4},{"id":"2","question":"La Asamblea General de Asociados es la responsable de conservar la memoria histórica, así como definir el futuro y permanencia de la Institución. Es el hilo conductor entre el origen y lo que se espera que sea la Institución en el futuro.","priority":1},{"id":"3","question":"La Asamblea General de Asociados define y/o aprueba la visión, misión y objetivos de la Institución en conjunto con los directivos y personal estratégico de la Institución.","priority":1},{"id":"4","question":"La Asamblea General de Asociados visita al menos 2 veces al año la Institución, recibiendo retroalimentación de sus colaboradores.","priority":4},{"id":"5","question":"La Asamblea General de Asociados  visita al menos 2 veces al año la Institución, recorre sus instalaciones e intercambia experiencias con sus beneficiarios de manera directa.","priority":1},{"id":"6","question":"La Asamblea General de Asociados  tiene lineamientos claramente definidos en la toma de decisiones, al menos dos terceras partes de los miembros activos vota para la toma de decisiones importantes para la Institución.","priority":1},{"id":"7","question":"Al menos dos terceras partes de la Asamblea General de Asociados  recauda fondos para la Institución o colabora de manera activa en las tareas de Procuración, facilitando la obtención de donativos en efectivo, en especie o servicios.","priority":4},{"id":"8","question":"La Asamblea General de Asociados  promueve el proyecto educativo y social de la Institución (visión, misión, programas).","priority":4},{"id":"9","question":"La Asamblea General de Asociados  brinda asesoría mensual o bimestral a los diferentes niveles de la Institución.","priority":4}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac1.2', 'Consejo Directivo / Mesa Directiva', 'ac-2018', 'Órgano de Gobierno', 'Consejo Directivo / Mesa Directiva', '1', '1.2', ' {"questions":[{"id":"10","question":"El Consejo Directivo/Mesa Directiva vigila el cumplimiento de las obligaciones fiscales de la Institución en tiempo y forma (Declaración Anual, Transparencia, IMSS, ISR, etc.).","priority":1},{"id":"11","question":"El Consejo Directivo/Mesa Directiva nombra al Director General de la Institución, considerando su formación académica, experiencia en el puesto y  pruebas definidas que garanticen la idoneidad del candidato.","priority":4},{"id":"12","question":"El Consejo Directivo/Mesa Directiva define, monitorea y fortalece los programas y servicios de la Institución, en cumplimiento de su Planeación Estratégica.","priority":1},{"id":"13","question":"El Consejo Directivo/Mesa Directiva presenta su Informe anual a sus beneficiarios, abriendo espacios concretos  para que externen sus dudas, cometarios y sugerencias.","priority":1},{"id":"14","question":"El Consejo Directivo/ Mesa Directiva promueve la realización de una planeación estratégica de la Institución al menos cada 6 años.","priority":4},{"id":"15","question":"El Consejo Directivo/Mesa Directiva ejecuta la planeación estratégica de la Institución (participa en las acciones de manera concreta).","priority":4},{"id":"16","question":"El Consejo Directivo/Mesa Directiva  apoya al Director General y evalúa su desempeño con un instrumento claro, que plantea sus objetivos anuales y niveles de cumplimiento.","priority":4},{"id":"17","question":"El Consejo Directivo/Mesa Directiva realiza o colabora en la elaboración de un presupuesto anual, posibles fuentes de financiamiento y estrategias generales para su obtención.","priority":4},{"id":"18","question":"El Consejo Directivo/Mesa Directiva recluta y capacita a nuevos consejeros al menos cada 6 años.","priority":4},{"id":"19","question":"El Consejo Directivo/Mesa Directiva  evalúa el desempeño de los consejeros considerando: su asistencia a reuniones; aportaciones económicas, en especie o servicios; asistencia a eventos de la Institución, entre otros.","priority":1},{"id":"20","question":"El Consejo Directivo/Mesa Directiva  crea los Comités de Apoyo y supervisa su labor al menos cada año, considerando los resultados de sus integrantes, así como de cada Comité; realizando los reconocimientos y ajustes que considere necesarios.","priority":1}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac1.3', 'Comités de apoyo', 'ac-2018', 'Órgano de Gobierno', 'Comités de apoyo', '1', '1.3', ' {"questions":[{"id":"21","question":"Su Institución cuenta con un Comité de Educación. Miembros de la Asamblea General de Asociados  con conocimientos, experiencia y pericia para asistir a la Institución en la toma de decisiones en materia educativa.","priority":1},{"id":"22","question":"Su Institución cuenta con un Comité de Administración y Finanzas. Miembros de la Asamblea General de Asociados  con conocimientos, experiencia y pericia para asistir a la Institución en la supervisión del manejo de recursos, fondos patrimoniales; prestaciones y estímulos a los colaboradores, entre otros.","priority":1},{"id":"23","question":"Su Institución cuenta con un Comité de Procuración de Fondos. Miembros de la Asamblea General de Asociados  con conocimientos, experiencia y pericia para asistir a la Institución en la búsqueda  donativos, acceso a convocatorias, apoyo en la elaboración y/o revisión reportes, campañas financieras, entre otros.","priority":1},{"id":"24","question":"Su Institución cuenta con un Comité de Estatus y Reglamentos. Miembros de la Asamblea General de Asociados  con conocimientos, experiencia y pericia para asistir a la Institución en la elaboración de reglamentos, contratos laborales y con proveedores, Aviso de Privacidad, entre otros.","priority":1}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac1.4', 'Director General', 'ac-2018', 'Órgano de Gobierno', 'Director General', '1', '1.4', ' {"questions":[{"id":"25","question":"El Director dirige la Institución en cumplimiento de su Plan Estratégico, supervisando que todos los programas, actividades y acciones sean congruentes con él (misión, visión y valores).","priority":4},{"id":"26","question":"El Director establece estrategias para asegurar la Normalidad Mínima de Operación Escolar.","priority":1},{"id":"27","question":"El Director da respuesta a la comunidad atendiendo las necesidades, problemáticas e inquietudes de manera asertiva, en tiempo y forma.","priority":4},{"id":"28","question":"El Director organiza reuniones mensuales donde se promueve la profesionalización docente y el intercambio de experiencias académicas mediante la reflexión colectiva , con el objetivo de convertir su centro de trabajo en una verdadera comunidad de aprendizaje.","priority":1},{"id":"29","question":"El Director recibe  dos capacitaciones, cursos o talleres durante el ciclo escolar, relacionadas con su labor profesional; de las cuales comparte experiencias y desprende acciones en favor de la Institución.","priority":4},{"id":"30","question":"El Director se reúne dos veces al año con las madres y padres de familia para dar a conocer las metas de aprendizaje que se esperan alcanzar, acciones a realizar, mecanismos de evaluación; con el propósito de involucrar a los padres en el proceso educativo de sus hijos.","priority":1},{"id":"31","question":"El Director coordina mensualmente la adquisición, distribución y asignación de recursos materiales, para el mejor funcionamiento de la Institución.","priority":4},{"id":"32","question":"El Director lleva a cabo intercambios semestrales de experiencias directivas con otras escuelas.","priority":1},{"id":"33","question":"El Director revisa y/o supervisa de manera periódica las planeaciones de los docentes asegurando la congruencia y pertinencia con su modelo educativo, la sobrecarga de tareas y gastos.","priority":1},{"id":"34","question":"El Director  realiza cada año una evaluación de su desempeño mediante un instrumento de 180o o 360o, el cual le da conocer aspectos a mejorar de su gestión.","priority":4},{"id":"35","question":"El Director se reúne mensualmente con los profesores para analizar las metas y acciones que permitan mejorar la escuela.","priority":1},{"id":"36","question":"El Director reconoce las fortalezas y debilidades académicas de la escuela, comunica al colegiado y define de manera conjunta las acciones para desarrollarlas o disminuirlas.","priority":4},{"id":"37","question":"El Director rinde cuentas al final del ciclo escolar, sobre la calidad académica y la administración de recursos a la comunidad educativa.","priority":1},{"id":"38","question":"El Director elabora y da seguimiento sistemático a un plan de desarrollo profesional de los docentes, evaluando en forma trimestral.","priority":1}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac1.5', 'Principios Institucionales', 'ac-2018', 'Órgano de Gobierno', 'Principios Institucionales', '1', '1.5', ' {"questions":[{"id":"39","question":"La Institución tiene definida una Misión, la cual se revisa y/o actualiza máximo cada 6 años.","priority":4},{"id":"40","question":"La Institución tiene definida su Visión, la cual se revisa y/o actualiza máximo cada 6 años.","priority":4},{"id":"41","question":"La Institución tiene definidos sus Valores, los cuales revisan y/o actualizan máximo cada 6 años. Estableciendo líneas de difusión y acción para que sean aplicados en todos los ámbitos de la Institución.","priority":1},{"id":"42","question":"La Institución tiene definida una Filosofía Institucional, la cual se revisa y/o actualiza máximo cada 6 años.","priority":1},{"id":"43","question":"La Institución tiene expuestas a la entrada de sus instalaciones su Misión, Visión y Valores, además de ser conocidos por el 100% de sus colaboradores.","priority":1}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac2.1', 'Legal', 'ac-2018', 'Marco Legal', 'Legal', '2', '2.1', ' {"questions":[{"id":"44","question":"La Institución posee Acta Constitutiva con las protocolizaciones correspondientes, inscritas en el Registro Público de la Propiedad, donde indiquen su objeto social, la lista de los miembros del Consejo Directivo o Patronato, sus cargos y funciones.","priority":1},{"id":"45","question":"La Asamblea General de Asociados conoce en qué consiste el Poder General de Dominio, cuál es su alcance y quienes están autorizados para ejercerlo.","priority":1},{"id":"46","question":"La Asamblea General de Asociados conoce en qué consiste el  Poder General para Actos de Administración, cuál es su alcance y quienes están autorizados para ejercerlo.","priority":1},{"id":"47","question":"La Asamblea General de Asociados conoce en qué consiste el Poder General para Pleitos y Cobranzas, cuál es su alcance y quienes están autorizados para ejercerlo.","priority":1},{"id":"48","question":"La Asamblea General de Asociados conoce en qué consiste el Poder para Otorgar y Suscribir Títulos de Crédito, cuál es su alcance y quienes están autorizados para ejercerlo.","priority":1},{"id":"49","question":"Todos los predios en los que se asienta la Institución están inscritos en el Registro Público de la Propiedad (ya sean de su propiedad, estén en comodato o renta).","priority":1}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac2.2', 'SAT', 'ac-2018', 'Marco Legal', 'SAT', '2', '2.2', ' {"questions":[{"id":"50","question":"La Institución posee un expediente físico y electrónico de los datos de su o sus representante(s) legal(es), RFC de la persona física e identificación oficial vigente para trámites y convocatorias.","priority":1},{"id":"51","question":"Tiene FIEL (firma electrónica), e.firma y e.firma portable, las cuales están debidamente resguardadas y son conocidas además de su Contador, por una persona de la Asamblea General de Asociados, Consejo o Dirección General.","priority":1},{"id":"52","question":"Tiene oficio de Autorización para recibir Donativos Deducibles de Impuestos en el Extranjero.","priority":1},{"id":"53","question":"La Institución conserva permanentemente su Autorización para recibir Donativos Deducibles de Impuestos desde los últimos 3 años.","priority":4},{"id":"54","question":"La Asamblea General de Asociados o el Director General, cuentan con el acuse de la Declaración de Transparencia del año pasado, la cual se realizó en tiempo y forma.","priority":1},{"id":"55","question":"La Asamblea General de Asociados o el Director General, cuentan con el acuse de la recepción de la Declaración de Anual del año pasado, la cual se realizó en tiempo y forma.","priority":1},{"id":"56","question":"La Asamblea General de Asociados o el Director General, cuentan con el acuse de la Declaración Informativa Múltiple (DIM) del año pasado, la cual se realizó en tiempo y forma.","priority":1},{"id":"57","question":"La Asamblea General de Asociados o el Director General, cuentan con el Aviso de Actividades Vulnerables  (Ley Antilavado) mensuales, los cuales se realizaron en tiempo y forma.","priority":1},{"id":"58","question":"La Asamblea General de Asociados  o el Director General dan seguimiento al envío de su Contabilidad Electrónica mensual al SAT los primeros 3 a 5 días hábiles del mes, con la información del catálogo de cuentas y la balanza de comprobación.","priority":1},{"id":"59","question":"La Asamblea General de Asociados  o el Director General cuentan con el acuse de la Opinión positiva en el Cumplimiento de Obligaciones Fiscales por el Servicio de Administración Tributaria (SAT).","priority":1},{"id":"60","question":"La Institución conserva un gasto máximo del 5% de los donativos recibidos en su gasto administrativo, de acuerdo al Reglamento de la Ley del Impuesto Sobre la Renta.","priority":1},{"id":"61","question":"La Institución cumple con las Normas de Información Financiera aplicables a las Entidades con Propósitos No Lucrativos (NIF B-16).","priority":1},{"id":"62","question":"La Institución recibe una opinión Sin Salvedades de parte del Auditor, en sus Estados Financieros Auditados y/o Dictamen Fiscal.","priority":1},{"id":"63","question":"En la Institución existe una persona remunerada con conocimientos suficientes para dar seguimiento a las actividades fiscales y contables de la Institución.","priority":4}]} ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac2.3', 'IMSS', 'ac-2018', 'Marco Legal', 'IMSS', '2', '2.3', ' {"questions":[{"id":"64","question":"Tiene a todos sus colaboradores incorporados al IMSS.","priority":1},{"id":"65","question":"Tiene la Tarjeta Patronal, con datos actualizados.","priority":1},{"id":"66","question":"Tiene Certificado Digital para altas y bajas del IDSE.","priority":1},{"id":"67","question":"Cumple con la Declaración de Prima de Riesgo de Trabajo en tiempo y forma.","priority":1},{"id":"68","question":"Cumplió en tiempo y forma con el pago mensual y bimestral de las cuotas obrero-patronales con el IMSS e INFONAVIT (respectivamente).","priority":1}]} ', 0, current_timestamp, current_timestamp);
