INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac2.7', 'Protección Civil', 'ac-2018', 'Marco Legal', 'Protección Civil', '2', '2.7', ' {"questions":[{"id":"79","question":"Todos los predios en los que se asienta la Institución cuentan con autorización vigente de Uso de Suelo.","priority":1},{"id":"80","question":"Todos los planteles cuentan con el Dictamen de Seguridad Estructural vigente.","priority":4},{"id":"81","question":"Todos los planteles cuentan con el Permiso de Protección Civil vigente.","priority":4},{"id":"82","question":"Todos los planteles cuentan con Programa Interno de Protección Civil.","priority":4},{"id":"83","question":"Todos los planteles cuentan con personal capacitado en Protección Civil.","priority":4},{"id":"84","question":"Cuenta con un Comité Interno de Seguridad Escolar (CISE) en cada plantel educativo de su Institución.","priority":4},{"id":"85","question":"El 100% de sus alumnos cuenta con Seguro Escolar contra Accidentes.","priority":1}]} ', 0, current_timestamp, current_timestamp);
