INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('2018-ac7.1', 'Docentes', 'ac-2018', 'Modelo Educativo', ' Docentes', '7', '7.1', ' {"questions":[{"id":"164","question":"El personal docente trabaja como un colectivo (titulares, maestros de idiomas, educación física, educación artística, entre otros), con propósitos académicos alineados a la Institución. ","priority":1},{"id":"165","question":"El personal de apoyo (auxiliares, cocina, servicio médico, personal administrativo y de mantenimiento), trabaja como un colectivo con el personal docente, alineándose a la misión y los  propósitos académicos de  la Institución.","priority":1},{"id":"166","question":"Los docentes se capacitan continuamente, se actualizan y aplican los conocimientos obtenidos en su práctica cotidiana, para la mejora de los aprendizajes de sus estudiantes.","priority":1},{"id":"167","question":"Los docentes conocen y comprenden los enfoques curriculares, planes, programas y contenidos, propios de su grado escolar o asignatura.","priority":4},{"id":"168","question":"Los docentes muestran capacidad crítica para la mejora de su desempeño.","priority":1},{"id":"169","question":"Los docentes planifican sus clases, considerando alternativas que toman en cuenta la diversidad de sus estudiantes.","priority":1},{"id":"170","question":"Los docentes muestran a los estudiantes confianza en sus capacidades y estimulan constantemente sus avances, esfuerzos y logros.","priority":1},{"id":"171","question":"Los docentes promueven en sus alumnos una participación activa, crítica y creativa como parte de su formación; de acuerdo a su edad y nivel de desarrollo. ","priority":4},{"id":"172","question":"Los docentes promueven su desarrollo profesional, mediante la reflexión colectiva y el intercambio de experiencias para convertir su centro de trabajo en una verdadera comunidad de aprendizaje