INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap1.1', 'El patronato', 'iap', 'Órgano de Gobierno', 'El Patronato', '1', '1.1', '{ "questions": [ { "id": "1", "question": "El Patronato es el Órgano Supremo de Gobierno de su Institución.", "priority": 1 }, { "id": "2", "question": "El Patronato está conformado por al menos dos personas que participan activamente en la toma de decisiones de la Institución.", "priority": 4 }, { "id": "3", "question": "El Patronato es el responsable de conservar la memoria histórica, así como definir el futuro y permanencia de la Institución. Es el hilo conductor entre el origen y lo que se espera que sea la Institución en el futuro.", "priority": 1 }, { "id": "4", "question": "El Patronato define y/o aprueba la visión, misión y objetivos de la Institución.", "priority": 1 }, { "id": "5", "question": "El Patronato aprueba la conformación del Consejo Directivo/Mesa Directiva de la Institución.", "priority": 1 }, { "id": "6", "question": "El Patronato aprueba las reglas de operación del Consejo Directivo/Mesa Directiva.", "priority": 1 }, { "id": "7", "question": "El Patronato orienta al Consejo Directivo/Mesa Directiva y aprueba sus principales decisiones.", "priority": 1 }, { "id": "8", "question": "El Patronato  está integrado por los fundadores y por personas que hayan demostrado su compromiso y fidelidad hacia la Organización.", "priority": 1 }, { "id": "9", "question": "El Patronato está alineado y vinculado a la realidad operativa de la Institución.", "priority": 4 }, { "id": "10", "question": "El Patronato apoya las actividades de la Institución en términos de procuración de recursos.", "priority": 4 }, { "id": "11", "question": "El Patronato promueve el proyecto educativo y social de la Institución (visión, misión, programas).", "priority": 4 }, { "id": "12", "question": "El Patronato brinda asesoría a los diferentes niveles de la Institución.", "priority": 1 }, { "id": "13", "question": "El Patronato recauda fondos para la Institución.", "priority": 4 } ] } ', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap1.2', 'Consejo Directivo / Mesa Directiva', 'iap', 'Órgano de Gobierno', 'Consejo Directivo / Mesa Directiva', '1', '1.2', '{ "questions": [ { "id": "14", "question": "El Consejo Directivo/Mesa Directiva presenta el informe anual de transparencia de la Institución.", "priority": 1 }, { "id": "15", "question": "El Consejo Directivo/Mesa Directiva nombra al Director General de la Institución.", "priority": 4 }, { "id": "16", "question": "El Consejo Directivo/Mesa Directiva define, monitorea y fortalece los programas y servicios de la Institución.", "priority": 1 }, { "id": "17", "question": "El Consejo Directivo/Mesa Directiva  asegura la integridad legal y ética de la Institución, ofreciendo rendición de cuentas a la sociedad y a los donantes.", "priority": 4 }, { "id": "18", "question": "El Consejo Directivo/ Mesa Directiva promueve la planeación estratégica de la Institución.", "priority": 4 }, { "id": "19", "question": "El Consejo Directivo/Mesa Directiva ejecuta la planeación estratégica de la Institución (participa en las acciones de manera concreta).", "priority": 4 }, { "id": "20", "question": "El Consejo Directivo/Mesa Directiva  apoya al Director General y evalúa su desempeño en la Institución.", "priority": 4 }, { "id": "21", "question": "El Consejo Directivo/Mesa Directiva  maneja los recursos de la Institución en forma eficiente y efectiva.", "priority": 1 }, { "id": "22", "question": "El Consejo Directivo/Mesa Directiva  recluta a nuevos consejeros.", "priority": 4 }, { "id": "23", "question": "El Consejo Directivo/Mesa Directiva orienta  a nuevos consejeros.", "priority": 1 }, { "id": "24", "question": "El Consejo Directivo/Mesa Directiva  evalúa el desempeño de los consejeros.", "priority": 1 }, { "id": "25", "question": "El Consejo Directivo/Mesa Directiva  crea y supervisa