INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap1.4', 'Director General', 'iap', 'Órgano de Gobierno', 'Director General', '1', '1.4', '{ "questions": [ { "id": "32", "question": "El Director dirige la Institución en cumplimiento de su visión, misión y Plan Estratégico.", "priority": 4 }, { "id": "33", "question": "El Director dirige el proyecto educativo de la Institución.", "priority": 1 }, { "id": "34", "question": "Establece estrategias para asegurar la Normalidad Mínima de Operación Escolar.", "priority": 1 }, { "id": "35", "question": "El Director ejerce liderazgo académico, organizativo, administrativo y social, para la transformación de la comunidad escolar.", "priority": 4 }, { "id": "36", "question": "El Director promueve el desarrollo profesional de sus colaboradores mediante la reflexión colectiva y el intercambio de experiencias, con el objetivo de convertir su centro de trabajo en una verdadera comunidad de aprendizaje.", "priority": 1 }, { "id": "37", "question": "El Director promueve para sí mismo un proceso permanente de formación y actualización.", "priority": 4 }, { "id": "38", "question": "El Director promueve que los docentes formen un solo equipo de trabajo.", "priority": 1 }, { "id": "39", "question": "El Director desarrolla una visión compartida entre la comunidad escolar sobre la mejora académica continua.", "priority": 1 }, { "id": "40", "question": "El Director gestiona permanentemente recursos para que la escuela funcione de la forma más adecuada posible.", "priority": 4 }, { "id": "41", "question": "El Director lleva a cabo intercambios de experiencias directivas con otras escuelas.", "priority": 1 }, { "id": "42", "question": "El Director dedica el tiempo necesario para supervisar las tareas académicas que respaldan el aprendizaje de los alumnos.", "priority": 1 }, { "id": "43", "question": "El Director sa