INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap4.1', 'Donantes', 'iap', 'Procuración de Fondos', 'Donantes', '4', '4.1', '{"questions":[{"id":"114","question":"Existe el área de Procuración de Fondos en la Institución con personal remunerado.","priority":4},{"id":"115","question":"¿Cuántas personas laboran en el área de Procuración de Fondos en la Institución?","options":[{"name":"1 persona","valuable":"true"},{"name":"2 personas","valuable":"true"},{"name":"3 personas","valuable":"true"},{"name":"Más de 3 personas","valuable":"true"},{"name":"No hay personal remunerado","valuable":"false"}],"priority":1},{"id":"116","question":"El personal dedicado a la Procuración de Fondos está capacitado para desarrollar y ejecutar el plan de procuración.","priority":4},{"id":"117","question":"El personal del área de Procuración de Fondos cuenta con experiencia para el desarrollo de sus actividades.","priority":4},{"id":"118","question":"El área de Procuración de Fondos cuenta con descripción de puestos.","priority":1},{"id":"119","question":"El personal del área de Procuración de Fondos conoce su descripción del puesto y sus responsabilidades.","priority":1},{"id":"120","question":"La Institución cuenta con un programa de voluntariados corporativos para atraer donantes a su Institución.","priority":1},{"id":"121","question":"En la Institución existe un plan o estrategia de Procuración de Fondos.","priority":4},{"id":"122","question":" El Plan de Procuración de Fondos incluye aspectos de organización, planeación, administración y control.","priority":1},{"id":"123","question":"Existe en su Institución un Plan Operativo de Procuración de Fondos anual detallado, que contemple calendario de procuración, cronograma de actividades, asignación de responsables y que esté alineado al presupuesto institucional.","priority":4},{"id":"124","question":"Se eje