INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap5.1', 'Procesos de gestión de Recursos Humanos', 'iap', 'Gestión de Recursos Humanos', 'Procesos de gestión de Recursos Humanos', '5', '5.1', '{"questions":[{"id":"138","question":"Tiene definidos los procesos de nómina, prestaciones y compensaciones.","priority":1},{"id":"139","question":"¿Su Institución aplica dichos criterios de nómina, prestaciones y compensaciones?","priority":1},{"id":"140","question":"Tiene definidos los procesos de alta y baja de colaboradores.","priority":1},{"id":"141","question":"¿Su Institución aplica dichos procesos de alta y baja de colaboradores?","priority":1},{"id":"142","question":"Tiene definidos los procesos de incrementos de sueldos.","priority":1},{"id":"143","question":"¿Su Institución aplica dichos procesos de incrementos de sueldos?","priority":1},{"id":"144","question":"Tiene definidos los procesos para reportar incidencias.","priority":1},{"id":"145","question":"¿Su Institución aplica dichos procesos para reportar incidencias?","priority":1},{"id":"146","question":"Tiene definidos los procesos para tramitar y reportar incapacidades.","priority":1},{"id":"147","question":"¿Su Institución aplica dichos procesos para tramitar y reportar incapacidades?","priority":1},{"id":"148","question":"Tienen definido un tabulador de sueldos.","priority":1},{"id":"149","question":"¿Su Institución aplica dicho tabulador de sueldos?","priority":1}]}', 0, current_timestamp, current_timestamp);
