INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap6.1', 'Instalaciones', 'iap', 'Infraestructura Física', 'Instalaciones', '6', '6.1', '{"questions":[{"id":"164","question":"Cuenta con las aulas necesarias para cubrir su demanda educativa.","priority":1},{"id":"165","question":"Cuenta con sanitarios en buenas condiciones para alumnos y docentes.","priority":1},{"id":"166","question":"Tiene aulas en condiciones que favorecen los procesos de enseñanza y aprendizaje.","priority":1},{"id":"167","question":"Cuenta con espacio y equipo que promueva el uso de las Tecnologías de la Información y Comunicación.","priority":1},{"id":"168","question":"Cuenta con espacios alternos que favorecen el aprendizaje de los alumnos: huerto, salón de cantos, danza, canchas, entre otros.","priority":1},{"id":"169","question":"Cuenta con instalaciones que garantizan un ambiente saludable y de seguridad para los alumnos.","priority":1}]}', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap6.2', 'Equipo y material didáctico', 'iap', 'Infraestructura Física', 'Equipo y material didáctico', '6', '6.2', '{"questions":[{"id":"170","question":"Cuenta con los materiales didácticos e insumos materiales para asegurar el logro de los objetivos y metas planeados.","priority":1},{"id":"171","question":"Establecen estrategias de cuidado y mantenimiento de edificio, mobiliario, laboratorios equipados y tecnología educativa.","priority":1},{"id":"172","question":"Utiliza medios tecnológicos para enriquecer el modelo educativo de la Institución.","priority":1},{"id":"173","question":"El equipo de cómputo de la Institución cuenta con sistema antivirus actualizado y con licencia de uso.","priority":1},{"id":"174","question":"Definen estrategias para el aprovechamiento de la papelería y de los insumos didácticos.","priority":1}]}', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap7.1', 'Docentes', 'iap', 'Modelo Educativo', ' Docentes', '7', '7.1', '{"questions":[{"id":"175","question":"El personal docente trabaja como un equipo integrado (maestro de educación especial, educación física, educación artística, entre otros), con intereses afines y metas comunes.","priority":1},{"id":"176","question":"El personal de apoyo trabaja como un equipo integrado con docentes, (maestro de educación especial, educación física, educación artística, entre otros), con intereses afines y metas comunes.","priority":1},{"id":"177","question":"Los docentes se capacitan continuamente, se actualizan y aplican los conocimientos obtenidos en su práctica cotidiana, para la mejora de los aprendizajes de sus estudiantes.","priority":1},{"id":"178","question":"Los docentes muestran un dominio pleno de los enfoques curriculares, planes, programas y contenidos.","priority":4},{"id":"179","question":"Los docentes muestran capacidad crítica para la mejora de su desempeño.","priority":1},{"id":"180","question":"Los docentes planifican sus clases, considerando alternativas que toman en cuenta la diversidad de sus estudiantes.","priority":1},{"id":"181","question":"Los docentes muestran a los estudiantes confianza en sus capacidades y estimulan constantemente sus avances, esfuerzos y logros.","priority":1},{"id":"182","question":"Los docentes consiguen de sus alumnos una participación activa, crítica y creativa como parte de su formación.","priority":4},{"id":"183","question":"Los docentes promueven su desarrollo profesional, mediante la reflexión colectiva y el intercambio de experiencias para convertir su centro de trabajo en una verdadera comunidad de aprendizaje.","priority":1},{"id":"184","question":"Los docentes buscan y están motivados para asistir a cursos que sean relevantes para su capacitación y actualización profesional.","priority":1},{"id":"185","question":"Los docentes conversan sobre los desempeños de los demás, apoyan en la definición de estrategias de mejora de las tareas docentes y se retroalimentan de manera profesional. ","priority":1},{"id":"186","question":"Los docentes evalúan los desempeños de los alumnos.","priority":1},{"id":"187","question":"Los docentes conversan para intercambiar experiencias sobre la planificación de sus clases.","priority":1},{"id":"188","question":"Los docentes comparten modalidades diferentes de su planeación didáctica para atender diferentes tipos de alumnos.","priority":1},{"id":"189","question":"Los docentes acuerdan el tipo de registro que se lleva de los alumnos con características especiales.","priority":1},{"id":"190","question":"Los docentes evalúan mutuamente su planeación didáctica.","priority":1},{"id":"191","question":"Los docentes analizan los resultados de los alumnos y los cambios que deben aplicar en su práctica pedagógica.","priority":1},{"id":"192","question":"Los docentes diseñan sus estrategias para atender eficazmente a los alumnos con rezago académico.","priority":1},{"id":"193","question":"Los docentes disponen de tiempo, espacio y materiales para los estudiantes que así lo requieren, de acuerdo a sus aptitudes, estilos y ritmos.","priority":1},{"id":"194","question":"Los docentes disponen e implementan actividades y ejercicios adaptados a las necesidades de los alumnos. ","priority":1},{"id":"195","question":"Los docentes se comunican con los padres de los alumnos para conversar con ellos sobre sus logros de aprendizaje.","priority":1},{"id":"196","question":"Los docentes están certificados en competencias laborales relacionadas con su actividad profesional.","priority":1},{"id":"197","question":"La Institución registra el porcentaje de rotación de sus docentes cada ciclo escolar e implementa acciones concretas para disminuirla.","priority":1},{"id":"198","question":"El porcentaje de rotación de sus docentes es inferior a un 20% anual.","priority":1}]}', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap7.2', 'Proceso de enseñanza-aprendizaje', 'iap', 'Modelo Educativo', 'Proceso de enseñanza-aprendizaje', '7', '7.2', '{"questions":[{"id":"199","question":"Las experiencias de aprendizaje propiciadas por los docentes ofrecen a los estudiantes oportunidades diferenciadas en función de sus diversas capacidades, aptitudes, estilos y ritmos.","priority":4},{"id":"200","question":"La Institución se abre a la integración de niñas y niños con necesidades educativas especiales, otorgando prioridad a los que presentan alguna discapacidad o aptitudes sobresalientes y que requieren apoyos específicos para desarrollar plenamente sus potencialidades.","priority":1},{"id":"201","question":"En la Institución implementan programas que promueven el conocimiento y valoración de nuestra realidad intercultural.","priority":1},{"id":"202","question":"La Institución implementa programas que promueven el cuidado de la salud.","priority":1},{"id":"203","question":"La Institución implementa programas de aprecio por el arte.","priority":1},{"id":"204","question":"La Institución implementa programas que promueven la preservación del ambiente.","priority":1},{"id":"205","question":"La Institución implementa programas que promueven el desarrollo de competencias lectoras.","priority":1},{"id":"206","question":"La Institución implementa programas que promueven el desarrollo de competencias matemáticas.","priority":1},{"id":"207","question":"La Institución implementa programas que promueven el desarrollo de competencias socio-emocionales.","priority":1},{"id":"208","question":"La Institución implementa programas de educación financiera.","priority":1},{"id":"209","question":"La Institución implementa programas que promueven la práctica de educación cívica.","priority":1}]}', 0, current_timestamp, current_timestamp);
