INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap7.4', 'Autogestión del aprendizaje', 'iap', 'Modelo Educativo', 'Autogestión del aprendizaje', '7', '7.4', '{"questions":[{"id":"212","question":"Los alumnos se organizan y participan activamente en las tareas sustantivas de la escuela.","priority":1},{"id":"213","question":"Los alumnos están informados sobre los objetivos de su aprendizaje. ","priority":1},{"id":"214","question":"Los alumnos diseñan parcial y gradualmente las estrategias y la ruta que han de seguir para su propio aprendizaje. ","priority":1},{"id":"215","question":"Los alumnos conocen sus limitaciones en el aprendizaje y piden apoyo de su formación cuando lo necesitan.","priority":1},{"id":"216","question":"Los alumnos tienen altas expectativas de su aprendizaje.","priority":1},{"id":"217","question":"Los alumnos son líderes en el desarrollo de proyectos como resultado de su aprendizaje.","priority":1}]}', 0, current_timestamp, current_timestamp);
INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap8.1', 'Evaluación', 'iap', 'Evaluación y calidad educativa', 'Evaluación', '8', '8.1', '{"questions":[{"id":"218","question":"La Institución tiene definido un perfil de egreso y orienta sus acciones para alcanzarlo.","priority":4},{"id":"219","question":"La Institución cumple con  la Normalidad Mínima Escolar propuesta por la SEP.","priority":1},{"id":"220","question":"La Institución evalúa el desarrollo de competencias en el Área de \"Lenguaje y comunicación\", especialmente referentes al lenguaje oral y escrito.","priority":1},{"id":"221","question":"La Institución evalúa el desarrollo de competencias en una \"Lengua Extranjera\".","priority":1},{"id":"222","question":"La Institución evalúa el desarrollo de competencias en el Área de \"Pensamiento matemático\".","priority":1},{"id":"223","question":"La Institución evalúa el desarrollo de competencias en el Área de \"Exploración del mundo natural y social\".","priority":1},{"id":"224","question":"La Institución evalúa el desarrollo de competencias a través de la práctica del deporte.","priority":1},{"id":"225","question":"La Institución evalúa el desarrollo de competencias a través de la práctica de la cultura y las artes.","priority":1},{"id":"226","question":"La Institución evalúa el desarrollo de  competencias emocionales y sociales.","priority":1},{"id":"227","question":"La Institución evalúa el desarrollo de  competencias a partir de la oferta educativa que hace en el Área de \"Autonomía Curricular\".","priority":1},{"id":"228","question":"La Institución evalúa las habilidades desarrolladas a través del uso de la tecnología.","priority":1},{"id":"229","question":"La Institución realiza ajustes metodológicos de acuerdo a las evaluaciones parciales durante el ciclo escolar.","priority":1},{"id":"230","question":"La Institución elaboró