INSERT INTO questionnaire_section(id, name, questionnaire_id, dimension, subdimension, dimension_id, subdimension_id, question_json, version, date_created, last_updated) VALUES('iap8.2', 'Perfil del alumnado y cuotas de recuperación', 'iap', 'Evaluación y calidad educativa', 'Perfil del alumnado y cuotas de recuperación', '8', '8.2', '{"questions":[{"id":"232","question":"La Institución realiza estudios socioeconómicos anuales a su población beneficiada.","priority":4},{"id":"233","question":"La Institución concede becas a sus beneficiarios de acuerdo a los estudios socioeconómicos que realiza.","priority":1},{"id":"234","question":"Al menos el 70% de su población beneficiada cuenta con beca total o parcial por los servicios educativos o asistenciales que recibe.","priority":4},{"id":"235","question":"La Institución informa/concientiza a su población beneficiada respecto al porcentaje de beca que recibe y los esfuerzos de la Institución por recibir donativos.","priority":1},{"id":"236","question":"Al menos 70% de las familias cubren en tiempo las cuotas de recuperación por los servicios educativos y asistenciales que reciben.","priority":1}]}', 0, current_timestamp, current_timestamp);
